// 짐 보관 영역
// 1. 안전 보관 서비스
.safe-storage {
  width: 100%;
  display: flex;
  flex-direction: column;

  .safe-title {
    font-weight: 600;
    font-size: $title-font-M;
    margin: 0 auto;
    margin-bottom: 70px;
    span {
      color: $primary-color;
    }
    @media screen and (max-width: 768px) {
      font-size: $title-font-S;
    }
    @media screen and (max-width: 390px) {
      font-size: $title-font-XS;
    }
  }
  .safe-index {
    width: 100%;
    display: flex;
    margin-bottom: 100px;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
    li {
      position: relative;
      width: calc(100% / 4);
      display: flex;

      align-items: center;
      justify-content: space-around;
      @media screen and (max-width: 768px) {
        width: calc(100% / 2) !important;
        max-width: 240px !important;
        margin-top: 20px;
      }
      .safe-list {
        width: 90%;
        max-width: 240px;
        min-width: 175px;
        max-height: 326px;
        border: 1px solid rgba(229, 229, 236, 1);
        border-radius: 10px;
        box-shadow: $info-boxShadow;
        padding: 0 25px;
        @media screen and (max-width: 430px) {
          min-width: 160px;
        }
        .safe-text {
          h4 {
            font-size: $text-font-XL;
            font-weight: bold;
            margin-top: 40px;
          }
          p {
            margin: 18px 0;
            font-size: $text-font-M;
            color: $font-gray;
            line-height: 1.4;
            min-height: 45px;
            display: flex;
            align-items: center;
            @media screen and (max-width: 430px) {
              font-size: $text-font-S;
            }
            span {
              strong {
                color: $font-primary;
                font-weight: 600;
              }
            }
          }
        }
        .safe-line {
          width: 100%;
          padding: 0 25px;
          height: 2px;
          background-color: $bg-light;
          margin: 0;
        }
        .safe-icon {
          display: flex;
          align-items: flex-start;
          justify-content: space-between;
          margin: 0;
          margin-top: 25px;
          padding: 0;
          span {
            font-weight: bold;
            font-size: $text-font-XL;
            color: $primary-color;
            @media screen and (max-width: 430px) {
              font-size: $text-font-L;
            }
          }
          img {
            margin-top: 25px;
            margin-bottom: 30px;
            @media screen and (max-width: 430px) {
              width: 70%;
            }
          }
        }
      }
      .safe-list1,
      .safe-list2,
      .safe-list3 {
        &::after {
          content: "";
          position: absolute;
          top: 50%;
          right: 0;
          transform: translate(50%, -50%);
          display: block;
          width: 16px;
          height: 24px;
          background: url("/images/lee/nextstep.png");
        }
      }
    }
  }
}

// 2. 보관함 위치 맵
.stroage-wrap {
  width: 100%;
